import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import profile from '../../images/profileIcon.svg';
import searchbar from '../../images/searchIcon.svg';

function Header(props) {
  const { title, onSearchChange, setIsSearchBarActive } = props;
  const [search, setSearch] = useState(false);
  const [inputSearch, setInputSearch] = useState('');

  function input() {
    if (!search) {
      setSearch(true);
      setIsSearchBarActive(true);
    } else {
      setSearch(false);
      setIsSearchBarActive(false);
    }
  }

  function setInput(e) {
    const text = e.target.value;
    setInputSearch(text);
  }
  useEffect(() => {
    onSearchChange(inputSearch);
  });
  return (
    <div>
      <Link to="/perfil">
        <img src={ profile } data-testid="profile-top-btn" alt="profileIcon" />
      </Link>

      <h1 data-testid="page-title">{title}</h1>
      <button
        type="button"
        onClick={ () => input() }
      >
        <img
          data-testid="search-top-btn"
          src={ searchbar }
          alt="search-icon"
        />
      </button>
      <div>
        { search && <input
          value={ inputSearch }
          onChange={ setInput }
          type="text"
          data-testid="search-input"
          placeholder="Buscar Receita"
        />}
      </div>
    </div>

  );
}
Header.propTypes = {
  title: PropTypes.string.isRequired,
  onSearchChange: PropTypes.func.isRequired,
  setIsSearchBarActive: PropTypes.func.isRequired,
};

export default Header;
