const streakIngredient = (key, event) => {
  console.log(key, event);
  const { target } = event;
  const element = document.getElementById(key);
  const lineTh = 'line-through';

  if (target.checked === true && element.style.textDecoration !== lineTh) {
    element.style.textDecoration = lineTh;
    localStorage.setItem(key, element.checked);
  }
  if (target.checked === false && element.style.textDecoration === lineTh) {
    element.style.textDecoration = '';
  }
};

const save = (key, event) => {
  const e = event;
  const checkbox = document.getElementById(key);
  localStorage.setItem(key, checkbox.checked);
};

const load = (key) => {
  const checked = JSON.parse(localStorage.getItem(key));
  document.getElementById(key).checked = checked;
};

export default {
  streakIngredient,
  save,
  load,
};
