import React, { useState } from 'react';
import '../../services/cocktails-api';

function SearchBar() {
  const [drink, setDrink] = useState('');
  function searchByDrinks(event) {
    if (event.target.value === 'Ingredientes') {
      setDrink(searchCocktailsByI('limon'));
    } else if (event.target.value === 'Nome') {
      setDrink(searchCocktailsByName());
    } else {
      setDrink(searchCocktailsByFirstLetter());
    }
  }

  return (
    <div>
      <div onChange={ (event) => searchByDrinks(event) }>
        <input
          data-testid="ingredient-search-radio"
          type="radio"
          value="Ingredientes"
          name="foods"
        />
        {' '}
        Ingredientes
        <input
          data-testid="name-search-radio"
          type="radio"
          value="Nome"
          name="foods"
        />
        {' '}
        Nome
        <input
          data-testid="first-letter-search-radio"
          type="radio"
          value="Primeira letra"
          name="foods"
        />
        {' '}
        Primeira letra
      </div>
      <div>
        <button
          type="button"
          data-testid="exec-search-btn"
        >
          Buscar
        </button>
      </div>
      <div>
        {drink}
      </div>
    </div>

  );
}

export default SearchBar;
