import React, { useState } from 'react';
import cocktails from '../../services/cocktails-api';

function SearchBar() {
  const [radio, setRadio] = useState('');
  function searchByIngredients(event) {
    if (event.target.value === 'ingredients') {
      setRadio(cocktails());
    }
  }

  return (
    <div>
      <div onChange={ (event) => searchByIngredients(event) }>
        <input
          data-testid="ingredient-search-radio"
          type="radio"
          value="Ingredientes"
          name="foods"
        />
        {' '}
        Ingredientes
        <input
          data-testid="name-search-radio"
          type="radio"
          value="Nome"
          name="foods"
        />
        {' '}
        Nome
        <input
          data-testid="first-letter-search-radio"
          type="radio"
          value="Primeira letra"
          name="foods"
        />
        {' '}
        Primeira letra
      </div>
      <div>
        <button
          type="button"
          data-testid="exec-search-btn"
        >
          Buscar
        </button>
      </div>
    </div>

  );
}

export default SearchBar;
